namespace Tests
{

    public class isValidDescriptionTest
    {
       
        [TestCase("description")]
        [TestCase(null)]
        [TestCase("    ")]
     
        public void TestDescription(string description)
        {
            //arrange
            Task task = new Task();
            //act
            bool ans = task.isValidDescription(description);
            //assert
            Assert.IsTrue(ans);

        }
    

        [TestCase("")]
        public void TestLoginFail(string description)
        {
            //arrange
            Task task = new Task();
            //act
            var ExceptionMessage = Assert.Throws<Exception>(() => task.isValidDescription(description));
            // assert
            Assert.That(ExceptionMessage.Message, Is.EqualTo("the description must have maximun 300 characters"));
        }
        
    }
